
public class interviewcapgemini 
{

	public static void main(String[] args) 
	{
		
		
		
		
		// TODO Auto-generated method stub
		int i;
		int j;
		int k;
		for(i=5;i>=1;i--)
		{
			for(j=1;j<i;j++)
			{
				System.out.print(" 1 ");
				for(k=j;k<=5;k++)
				{
					System.out.println(k+"k");
				}
			}
			System.out.println(i+"i");
			
			
		
		}
	}
	

}
